INSERT [dbo].[Bulletins] ([Id], [title], [content],[NewsType], [StartDate], [EndDate], [CreateDate], [CreateBy], [ModifyDate], [ModifyBy], [IsDelete]) VALUES (56, N'2013年度第二期志工基礎教育訓練課程預計於6/22、6/23兩日辦理，即日起開放報名，請躍參加!!', N'<p>&nbsp;</p>
<p>&nbsp;</p>
<div style="line-height: 15pt; text-indent: -91pt; margin: 0cm 0cm 0pt 91pt"><span style="color: #0000ff"><span style="font-family: 標楷體"><span style="font-size: 13pt">一、目&nbsp;&nbsp;&nbsp; 的：為提昇新竹縣志願服務工作人員的專業常識及意願，充實服務內涵，以精進志工知能為主，期以強化服務工作品質，保障受服務者之權益，以達增進社會祥和樂利之目標。</span></span></span></div>
<div style="line-height: 15pt"><span style="color: #0000ff"><span style="font-family: 標楷體"><span style="font-size: 13pt">二、指導單位：新竹縣政府、新竹縣議會</span></span></span></div>
<div style="line-height: 15pt"><span style="color: #0000ff"><span style="font-family: 標楷體"><span style="font-size: 13pt">三、主辦單位：社團法人新竹縣志願服務協會</span></span></span></div>
<div style="line-height: 15pt"><span style="color: #0000ff"><span style="font-family: 標楷體"><span style="font-size: 13pt">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 13pt">協辦單位：新竹縣志願服務推廣中心、新竹縣各志願服務團體單位</span></span></span></div>
<div style="line-height: 15pt"><span style="color: #0000ff"><span style="font-family: 標楷體"><span style="font-size: 13pt">四、參加對象：新竹縣祥和計畫志工隊、各社區、學校、社團等志願服務人員，合計100人。</span></span></span></div>
<div><span style="color: #0000ff"><span style="font-family: 標楷體"><span style="font-size: 13pt">五、研習時間：2013年6月22日(六) 8:00-17:00及6月23日(日)8:00-12:00</span></span></span></div>
<div style="layout-grid-mode: char"><span style="color: #0000ff"><span style="font-family: 標楷體"><span style="font-size: 13pt">六、研習地點：新竹縣婦幼館一樓中型會議室 (地址：新竹縣竹北市縣政二路620號)</span></span></span></div>
<div style="line-height: 15pt"><span style="color: #0000ff"><span style="font-family: 標楷體"><span style="font-size: 13pt">七、報名日期：<b>自即日起至額滿截止(共100名)</b></span></span></span></div>
<div style="line-height: 18pt"><span style="color: #0000ff"><span style="font-family: 標楷體"><span style="font-size: 13pt">八、報名方式：(1)填具報名表親至新竹縣婦幼館內「志工推廣中心」辦公室報名。</span></span></span></div>
<div style="line-height: 18pt; margin: 0cm 0cm 0pt 12pt"><span style="color: #0000ff"><span style="font-family: 標楷體"><span style="font-size: 13pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (2)</span><span style="font-size: 13pt">傳真報名：03-55201923。</span></span></span></div>
<div style="line-height: 15pt; text-indent: -91pt; margin: 0cm 0cm 0pt 91pt"><span style="color: #0000ff"><span style="font-family: 標楷體"><span style="font-size: 13pt">九、</span><span style="font-size: 13pt">報名手續：每人繳交</span><span style="font-size: 13pt">報名費300元（內含講師費、印刷費、證書印製、佈置費、雜支）及兩吋照片一張（照片用於證書製作），報名費用、照片繳交期限為開課前一週請務必完成，否則視同未完成報名手續。</span></span></span></div>
<div style="line-height: 15pt"><span style="color: #0000ff"><span style="font-family: 標楷體"><span style="font-size: 13pt">十、完整參加訓練者，頒給基礎教育訓練結業證書，如有任何疑問歡迎來電洽詢03-55201923。</span></span></span></div>
<div style="line-height: 15pt; text-indent: -97.5pt; margin: 0cm 0cm 0pt 97.5pt"><span style="color: #0000ff"><span style="font-family: 標楷體"><span style="font-size: 13pt">十一、研習課程：邀請政府官員、專家學者、資深志工擔任講師，分享志願服務的內容與實作研習，讓參與志工快樂學習成長，獲益良多。</span></span></span></div>
