INSERT [dbo].[Bulletins] ([Id], [title], [content],[NewsType], [StartDate], [EndDate], [CreateDate], [CreateBy], [ModifyDate], [ModifyBy], [IsDelete]) VALUES (93, N'新竹榮民服務處榮欣志工隊招募', N'<p>&nbsp;</p>
<div style="text-align: center; line-height: 150%" align="center"><strong><span style="line-height: 150%; font-size: 18pt">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style="font-family: 標楷體"><span style="line-height: 150%; font-size: 18pt"> </span><span style="line-height: 150%; font-size: 18pt">新竹榮民服務處榮欣志工隊 </span></span></strong></div>
<div style="text-align: center; line-height: 150%" align="center"><span style="font-family: 標楷體"><strong><span style="line-height: 150%; font-size: 18pt">&nbsp;&nbsp;&nbsp; </span><span style="line-height: 150%; font-size: 18pt">志工招募簡章</span></strong></span></div>
<div style="text-align: center; line-height: 150%" align="center"><span style="font-family: 標楷體">&nbsp;</span></div>
<div style="text-indent: -36pt; margin: 0cm 0cm 0pt 36pt"><span style="font-family: 標楷體"><span style="font-size: 13pt; font-weight: normal">一、<span style="font: 7pt ''Times New Roman''">&nbsp;&nbsp; </span></span><span style="font-size: 13pt; font-weight: normal">資格條件:</span></span></div>
<div style="text-indent: -35.35pt; margin: 0cm 0cm 0pt 63.7pt"><span style="font-family: 標楷體"><span style="font-size: 13pt; font-weight: normal">(一)<span style="font: 7pt ''Times New Roman''">&nbsp;&nbsp; </span></span><span style="font-size: 13pt; font-weight: normal">具榮民、榮眷身分或社會熱心人士，年滿15歲以上65歲以下，品行端正熱心公益，認同本隊服務宗旨者得以申請加入志工行列。</span></span></div>
<div style="text-indent: -35.35pt; margin: 0cm 0cm 0pt 63.7pt"><span style="font-family: 標楷體"><span style="font-size: 13pt; font-weight: normal">(二)<span style="font: 7pt ''Times New Roman''">&nbsp;&nbsp; </span></span><span style="font-size: 13pt; font-weight: normal">能利用個人閒暇之餘參與服務工作，且具服務熱忱，身心健康足以擔任各項服務工作者。</span></span></div>
<div style="text-indent: -36pt; margin: 0cm 0cm 0pt 36pt"><span style="font-family: 標楷體"><span style="font-size: 13pt; font-weight: normal">二、<span style="font: 7pt ''Times New Roman''">&nbsp;&nbsp; </span></span><span style="font-size: 13pt; font-weight: normal">服務對象：獨居或單身年長榮民、榮民遺眷，一般榮民、榮眷。</span></span></div>
<div style="text-indent: -36pt; margin: 0cm 0cm 0pt 36pt"><span style="font-family: 標楷體"><span style="font-size: 13pt; font-weight: normal">三、<span style="font: 7pt ''Times New Roman''">&nbsp;&nbsp; </span></span><span style="font-size: 13pt; font-weight: normal">服務項目:家事協助、就醫陪伴、友善訪視、修繕服務、代辦服務、電話問安、儀容修整、送餐服務、環境改善、危機協處、防騙宣導等居家服務。</span></span></div>
<div style="text-indent: -36pt; margin: 0cm 0cm 0pt 36pt"><span style="font-family: 標楷體"><span style="font-size: 13pt; font-weight: normal">四、<span style="font: 7pt ''Times New Roman''">&nbsp;&nbsp; </span></span><span style="font-size: 13pt; font-weight: normal">榮欣志工服務隊，需接受基礎訓練及特殊訓練各12小時後發給志願服務手冊，服務滿36小時後，得發給志工服務證。</span></span></div>
<div style="text-indent: -36pt; margin: 0cm 0cm 0pt 36pt"><span style="font-family: 標楷體"><span style="font-size: 13pt; font-weight: normal">五、<span style="font: 7pt ''Times New Roman''">&nbsp;&nbsp; </span></span><span style="font-size: 13pt; font-weight: normal">志工服務年資滿1年且服務時數達150小時以上者得向志願服務運用單位提出申請服務績效證明。</span></span></div>
<div style="text-indent: -36pt; margin: 0cm 0cm 0pt 36pt"><span style="font-family: 標楷體"><span style="font-size: 13pt; font-weight: normal">六、<span style="font: 7pt ''Times New Roman''">&nbsp;&nbsp; </span></span><span style="font-size: 13pt; font-weight: normal">凡參與服務滿3年且服務總時數達300小時以上，績效優良者得推薦表揚，並核發志願服務榮譽卡。其餘依志願服務法及志願服務獎勵辦法辦理。</span></span></div>
