INSERT [dbo].[Bulletins] ([Id], [title], [content],[NewsType], [StartDate], [EndDate], [CreateDate], [CreateBy], [ModifyDate], [ModifyBy], [IsDelete]) VALUES (96, N'2014年新竹縣志願服務協會辦理第一期志工基礎教育訓練，即日起開放報名，請踴躍參加!!', N'<p>&nbsp;</p>
