INSERT [dbo].[Bulletins] ([Id], [title], [content],[NewsType], [StartDate], [EndDate], [CreateDate], [CreateBy], [ModifyDate], [ModifyBy], [IsDelete]) VALUES (159, N'新竹縣政府2016年推動以客為尊-Hakka樂學趣---海陸腔客語研習', N'<p>&nbsp;</p>
<div><span style="font-size: 20pt"><strong>新竹縣政府2016年推動以客為尊-Hakka樂學趣</strong></span></div>
<p><span style="font-size: 20pt"><strong>
<div align="center"><u><span style="font-size: 20pt">海陸腔客語研習</span></u></div>
<b><span style="font-size: 20pt">
<div style="text-justify: inter-ideograph; text-align: justify; line-height: 24pt; text-indent: -36pt; margin: 0cm 0cm 0pt 36pt"><span style="font-size: 16pt">一、</span><span style="font-size: 16pt">課程名稱：海陸腔客語研習。</span></div>
<div style="text-justify: inter-ideograph; text-align: justify; line-height: 24pt; text-indent: -36pt; margin: 0cm 0cm 0pt 36pt"><span style="font-size: 16pt">二、</span><span style="font-size: 16pt">承辦單位：新竹縣政府人事處。</span></div>
<div style="text-justify: inter-ideograph; text-align: justify; line-height: 24pt; text-indent: -36pt; margin: 0cm 0cm 0pt 36pt"><span style="font-size: 16pt">三、</span><span style="font-size: 16pt">研習地點：新竹縣政府3樓第2會議室。<br />
（新竹縣竹北市光明六路10號3樓）</span></div>
<div style="text-justify: inter-ideograph; text-align: justify; line-height: 24pt; text-indent: -36pt; margin: 0cm 0cm 0pt 36pt"><span style="font-size: 16pt">四、</span><span style="font-size: 16pt">研習期間：自2016年5月17日起至2016年8月24日止，總計研習時數為48小時。</span></div>
<div style="text-justify: inter-ideograph; text-align: justify; line-height: 24pt; text-indent: -36pt; margin: 0cm 0cm 0pt 36pt"><span style="font-size: 16pt">五、</span><span style="font-size: 16pt">參加對象：對客語有興趣之現職公務員工、參與新竹縣志願服務之志工、新竹縣各機關學校退休之公教人員。</span></div>
<div style="text-justify: inter-ideograph; text-align: justify; line-height: 24pt; text-indent: -36pt; margin: 0cm 0cm 0pt 36pt"><span style="font-size: 16pt">六、</span><span style="font-size: 16pt">報名日期：自即日起至2016年5月6日（額滿即停止報名）。</span></div>
<div style="text-justify: inter-ideograph; text-align: justify; line-height: 24pt; text-indent: -36pt; margin: 0cm 0cm 0pt 36pt"><span style="font-size: 16pt">七、</span><span style="font-size: 16pt">報名方式：</span></div>
<div style="text-justify: inter-ideograph; text-align: justify; line-height: 24pt; text-indent: -36.9pt; margin: 0cm 0cm 0pt 60.9pt"><span style="font-size: 16pt">(一)</span><span style="font-size: 16pt">請填妥報名表後傳真（FAX：5519043）至新竹縣政府人事處，並來電確認是否錄取；如經錄取者請依下列日期時間至研習地點上課，不再另行通知。</span></div>
<div style="text-justify: inter-ideograph; text-align: justify; line-height: 24pt; text-indent: -36.9pt; margin: 0cm 0cm 0pt 60.9pt"><span style="font-size: 16pt">(二)</span><span style="font-size: 16pt">新竹縣政府及所屬各機關學校（含鄉鎮市公所及代表會）現職公務員工仍請至本縣數位人事服務網／訓練進修／研習訊息項下報名（課程代碼A20160053）。</span></div>
<div align="center">&nbsp;</div>
<div align="center">欲知詳情請參閱附件</div>
<p>&nbsp;</p>
</span></b></strong></span></p>
<p>&nbsp;</p>
<div>&nbsp;</div>', 0, N'2016-04-01', N'2016-05-06', CAST(N'2016-04-27T10:08:00' AS SmallDateTime), NULL, NULL, NULL, 0)
