INSERT [dbo].[Bulletins] ([Id], [title], [content],[NewsType], [StartDate], [EndDate], [CreateDate], [CreateBy], [ModifyDate], [ModifyBy], [IsDelete]) VALUES (400, N'新竹縣學生校外生活輔導會 2021 年度「迎向春暉 認輔志工」志工招募', N'<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm; text-align:center"><span style="color:#c0392b"><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><span style="font-size:14.0pt"><span style="font-family:華康儷特圓">新竹縣學生校外生活輔導會 2021 年度「迎向春暉 認輔志工」實施計畫</span></span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm">&nbsp;</p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="color:#e67e22"><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">壹、依據</span></strong></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">一、教育部 2018 年 8 月 24 日臺教學(五)字第 20180133735B 號令「教育部春</span></strong></span></span><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">暉志工服務實施要點」。</span></strong></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">二、教育部「防制學生藥物濫用三級預防實施計畫暨輔導作業流程」。</span></strong></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">三、中華民國 2020 年 1 月 15 日「志願服務法」辦理。</span></strong></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="color:#e67e22"><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書"><span style="background-color:#ffffff">貳、目的</span></span></strong></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">一、依志願服務法規定，召募本縣具服務熱誠之志工，協助輔導高級中等以</span></strong></span></span><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">下學校濫用藥物之高關懷學生並針對特定人員及早介入輔導與關懷，培</span></strong></span></span><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">養正當休閒興趣與拒絕毒品誘惑之技巧，協助校外會輔導行政支援及協</span></strong></span></span><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">同解決困擾問題。</span></strong></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">二、透過藥物濫用專業知能培訓之認輔志工，協助本縣學校內藥物濫用學</span></strong></span></span><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">生，協同春暉小組輔導戒治，適時給予關懷輔導，讓學生遠離毒害，再</span></strong></span></span><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">造清新健康之無毒校園環境。</span></strong></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">三、運用社會資源，廣納熱心志工，鼓勵投入認輔人力，針對學校內適應困</span></strong></span></span><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">難、行為偏差之中輟復學生及對「濫用藥物」學生施予關懷照顧，協助</span></strong></span></span><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">順利成長與發展。</span></strong></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書"><span style="color:#e67e22">參、期程：</span></span></strong></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">自計畫實施起。</span></strong></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書"><span style="color:#e67e22">肆、招募人數：</span></span></strong></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:12pt"><span style="font-family:Calibri,sans-serif"><strong><span style="font-family:華康儷楷書">預計 15 人。</span></strong></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm">&nbsp;</p>
