INSERT [dbo].[Bulletins] ([Id], [title], [content],[NewsType], [StartDate], [EndDate], [CreateDate], [CreateBy], [ModifyDate], [ModifyBy], [IsDelete]) VALUES (444, N'【訊息轉知】新竹縣動物保護教育園區 志工召募活動開跑囉!', N'<p><a href="https://apc.hsinchu.gov.tw/%e6%96%b0%e7%ab%b9%e7%b8%a3%e5%8b%95%e7%89%a9%e4%bf%9d%e8%ad%b7%e6%95%99%e8%82%b2%e5%9c%92%e5%8d%80-%e5%bf%97%e5%b7%a5%e5%8f%ac%e5%8b%9f%e6%b4%bb%e5%8b%95%e9%96%8b%e8%b7%91%e5%9b%89/" target="_blank"><strong>新竹縣動物保護教育園區</strong>&nbsp;<strong>志工召募活動開跑囉!</strong></a></p>

<p>最近收容所常接到愛心民眾來電詢問---要如何成為收容所的志工?為了這些殷殷企盼的民眾!&nbsp;我們努力催生~~終於!!!志工召募計畫誕生了</p>

<p>原本收容所正值改建時期，有許多龐雜的事務正如火如荼地進行中。然而依照「志願服務法」成立志工隊和浪浪一同迎接嶄新的動物保護教育園區落成，一直是我們努力的目標!</p>

<p><span style="color:#e74c3c">我們將在<strong>1/15(</strong><strong>六)上午9:30</strong>於本所二樓會議室舉辦<strong>新竹縣公立動物收容所志工聯繫暨召募說明會</strong>，屆時歡迎願意加入志工行列的舊朋友與新朋友一同來參與。</span></p>

<p><span style="color:#e74c3c">另外有關<strong>志工訓練課程預訂於2/26(六)9:00-16:00</strong>舉行，請有意願加入志願服務的民眾務必把時間空出來參與訓練課程，完成計畫內所載相關訓練課程及實習時數，方能依規定正式成為志工喔!</span></p>

<p><strong>報名方式:請至本所(新竹縣家畜疾病防治所)官網詳閱計畫書並下載報名表，填妥後檢附相關文件資料以郵寄或親送方式遞送，並於信封註明「報名志工資料」。</strong></p>

<p><strong>報名期間:<u>公告日起至2022年2月18日止</u>(以郵戳或家畜所收發室圓戳章為憑)或召募人數達50人止。</strong></p>

<p>希望未來有更多喜愛毛孩的朋友可以踴躍加入我們，投入志願服務的行列給浪浪們更多愛與溫暖，並且找到屬於牠們幸福的家。</p>

<p>浪愛圓滿~一同建構友善動物城市，發揚動物保護精神投入生命教育，使這份愛落地生根，走入家庭走入校園，讓我們以身作則並將愛的種子傳承給下一代。</p>
', 1, N'2022-01-11', N'2022-02-26', CAST(N'2022-01-11T17:11:00' AS SmallDateTime), NULL, NULL, NULL, 0)
