INSERT [dbo].[Bulletins] ([Id], [title], [content],[NewsType], [StartDate], [EndDate], [CreateDate], [CreateBy], [ModifyDate], [ModifyBy], [IsDelete]) VALUES (471, N'★當我發現衛福部志願服務整合系統的資料有誤，怎麼辦？或是更換承辦人？', N'<p>TO夥伴日安！</p>

