INSERT [dbo].[Bulletins] ([Id], [title], [content],[NewsType], [StartDate], [EndDate], [CreateDate], [CreateBy], [ModifyDate], [ModifyBy], [IsDelete]) VALUES (489, N'2022年度社福類志願服務運用單位評鑑', N'<p><span style="font-size:16px">本縣已推展社福類志願服務工作2年以上之運用單位，分為2組進行評鑑(採自願參加)，分組方式如下：</span></p>

<p><span style="font-size:16px">1.社區發展組。</span></p>

<p><span style="font-size:16px">2.社團法人/財團法人/公部門組。</span></p>

<p><span style="font-size:16px"><strong>評鑑項目：（詳見評分表）</strong></span></p>

<p><span style="font-size:16px">1.基本指標&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(30分)</span></p>

<p><span style="font-size:16px">2.組織與管理&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;(35分)</span></p>

<p><span style="font-size:16px">3.服務績效&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (35分)</span></p>

<p><span style="font-size:16px">4.加分項目&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;(20分)</span></p>

<p>&nbsp;</p>

<p><strong><span style="font-size:16px">審查階段：</span></strong></p>

<p><span style="font-size:16px">1.初評：於2022年4-6月辦理，請參加評鑑之志願服務運用單位於2022年5月20日前提供書面資料，本處召開初評會議，2組各取6個單位進入複評。</span></p>

<p><span style="font-size:16px">2.複評：於2022年7-9月辦理，由本處召開複評會議，請複評單位進行簡報，評定受評單位成績。</span></p>

<p><span style="font-size:16px">3.獎勵表揚：於2022年10-12月辦理，結合本府2022年度志工大會師暨表揚活動或其他適合公開場合表揚。</span></p>

<p>&nbsp;</p>

