INSERT [dbo].[Bulletins] ([Id], [title], [content],[NewsType], [StartDate], [EndDate], [CreateDate], [CreateBy], [ModifyDate], [ModifyBy], [IsDelete]) VALUES (542, N'新竹企業志工理念交流會 展現助人創新動能(已截止)', N'<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><strong><span style="font-size:16.0pt"><span style="font-family:&quot;新細明體&quot;,serif"><span style="color:#212529">新竹企業志工理念交流會 展現助人創新動能</span></span></span></strong></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm">&nbsp;</p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="color:null"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:16.0pt"><span style="font-family:Gungsuh">《新竹縣企業志工推廣理念交流會》於 8 月 27 日登場，新竹縣志願服務推廣中心（以下簡稱志推中心）至關西百年茶廠-錦泰茶廠辦理此分享會，享受沏一壺茶的美好時光，聊聊企業志工服務如何集結眾人之力，推廣公益的延續。交流會邀請聯發科技志工社閱讀寫作</span></span><span style="font-size:16.0pt"><span style="font-family:&quot;新細明體&quot;,serif">教育</span></span><span style="font-size:16.0pt"><span style="font-family:Gungsuh">計畫召集人吳孟霖</span></span><span style="font-size:16.0pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Rick </span></span><span style="font-size:16.0pt"><span style="font-family:Gungsuh">分享推動閱讀寫作</span></span><span style="font-size:16.0pt"><span style="font-family:&quot;新細明體&quot;,serif">教育</span></span><span style="font-size:16.0pt"><span style="font-family:Gungsuh">計畫的初心，以及台灣晶技志工社鄭喜友社長分享近三年的志工社團經營的經驗談。</span></span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm">&nbsp;</p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="color:null"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:16.0pt"><span style="font-family:Gungsuh">新竹縣政府社會處李國祿處長感謝在座企業代表的專業分享，也提及新竹縣很重視企業志工的推廣，因此今年在志推中心的協助下，集結了各企業志工社代表前來交流。</span></span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm">&nbsp;</p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="color:null"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:16.0pt"><span style="font-family:Gungsuh">嘉南藥理大學社會工作系助理教授盧禹璁引言，近年CSR（Corporate Social Responsibility）的推動，多數企業以自身專長結合社會責任推動志願服務，實踐企業志工的價值。同時盧老師也提到台灣邁入高齡社會，以及少子女化的狀況下，企業志工活動要如何改變。</span></span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="color:null"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:16.0pt"><span style="font-family:Gungsuh"><img alt="" src="/FielUpLoad/userfiles/images/312512(1).jpg" style="height:197px; width:350px" /></span></span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><img alt="" src="/FielUpLoad/userfiles/images/312522(2).jpg" style="height:197px; width:350px" /></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm">&nbsp;</p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:16.0pt"><span style="font-family:Gungsuh">緊接著為企業志工領導者與談，<span style="color:#212529">吳孟霖</span>談及閱讀寫作教育計畫，強調其實學生不是「無法改變」，而是自己「決定不改變」。因為疫情，此計畫開始推動寫作服務線上化，志工錄製線上課程讓學員及偏鄉孩童受益。同時在學習系統中規劃遊戲學習的任務包和升級機制，讓學生找回對寫作的樂趣。未來也期待透過「AI助手智慧輔助」降低教師修改文章的時間，以科技創新的方式擔任企業與學校的橋樑。</span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif">&nbsp;</span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:16.0pt"><span style="font-family:Gungsuh">鄭喜友社長提及，「從事志工一定會有所成長，心靈是富足的，很難用筆墨形容。」他期盼帶領社員往一致目標前進，實踐社福的專業。平時也會安排志工到育幼院、療養院等機構服務，推動志工基礎教育訓練，同時也安排相關公益講座讓志工們相互學習。</span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif">&nbsp;</span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:16.0pt"><span style="font-family:Gungsuh">活動最後，台灣公益組織教育基金會董事洪健庭強調，和學子合作就必須尊重孩子的天賦與他們的想法，社會創新的動能便能更強大。其中，在座偕同推廣志工服務的高中教師Julia 也認同，要讓青少年了解志工服務並不是只有淨灘、護理之家服務，還能以不同的方式參與。</span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:16.0pt"><span style="font-family:Gungsuh"><img alt="" src="/FielUpLoad/userfiles/images/312640.jpg" style="height:197px; width:350px" /></span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif">&nbsp;</span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:16.0pt"><span style="font-family:Gungsuh">與會的兩間公司的志工服務形式較不相同，<span style="color:#212529">聯發科技志工社</span>以人為本，透過科技輔助實踐志工服務，而<span style="color:#212529">台灣晶技志工社</span>帶領志工實踐社福的專業，親力親為齊心做公益來回饋社會。如果企業有想推動志願服務或相關資源想投入社會服務，可聯繫03- 55201623 新竹縣志願服務推廣中心督導張淑婷。</span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm">&nbsp;</p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:16.0pt"><span style="font-family:Gungsuh">採訪編輯：吳珮瑜</span></span></p>
', 0, N'2022-09-05', N'2023-12-31', CAST(N'2022-09-05T09:08:00' AS SmallDateTime), NULL, NULL, NULL, 0)
