INSERT [dbo].[Bulletins] ([Id], [title], [content],[NewsType], [StartDate], [EndDate], [CreateDate], [CreateBy], [ModifyDate], [ModifyBy], [IsDelete]) VALUES (543, N'公民實踐志工服務 整理漂書、仙草田堆肥翻堆', N'<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:14.0pt"><span style="font-family:Gungsuh"><span style="color:#212529">《新竹縣企業志工推廣理念交流會》於 8 月 27 日登場，上午的交流會結束後，主辦單位-新竹縣志願服務推廣中心（委託雙福基金會經營管理），邀請各企業員工與其親屬朋友參與志工服務體驗。本次活動有兩項服務內容，《好書好用》舉辦在新竹縣關西鎮，稱為全台最短的中正路上的石店子 69 有機書店，而《好田好景》則是讓大夥協助農民，替仙草田除草、堆肥翻堆，期待志工們在行動中找回善的初心。</span></span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm">&nbsp;</p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:14.0pt"><span style="font-family:Gungsuh">志工服務行前說明，邀請<span style="color:#212529">石店子 69 有機書店的店長徐紫珉，解說本次的服務內容，本次體驗服務即希望志工替書店整理大量的捐贈書籍。同時徐紫珉也提到創辦人羅文鈞先生</span>為什麼會想開有機書店？因為<span style="color:#212529">石店子 69 有機書店的</span>核心價值在於讓更多人看好書，實踐「以書換書」的美好循環，讓書可以去到更多的地方。</span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:14.0pt"><span style="font-family:Gungsuh"><img alt="" src="/FielUpLoad/userfiles/images/LINE_ALBUM_%E7%85%A7%E7%89%87%E5%8E%9F%E6%AA%94_220901_3_0.jpg" style="height:225px; width:300px" /><img alt="" src="/FielUpLoad/userfiles/images/LINE_ALBUM_%E7%85%A7%E7%89%87%E5%8E%9F%E6%AA%94_220901_2_0.jpg" style="height:225px; width:300px" /></span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm">&nbsp;</p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:14.0pt"><span style="font-family:Gungsuh"><span style="color:#212529">關西鎮鄉土文化協會理事長邱美鳳提及，協會致力於守護文化資產，集結「仙草志工」一起為「仙草一分田」除草，讓這塊僅約一分多地的仙草田，能在年底變成浪漫的紫色花田。同時，協會也有創辦社區刊物《牛</span></span></span><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:14.0pt"><span style="font-family:Gungsuh"><span style="color:#212529">河畔》，此刊物以關西在地人文為主軸，分享關西人的「集體記憶」。</span></span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:14.0pt"><span style="font-family:Gungsuh"><span style="color:#212529"><img alt="" src="/FielUpLoad/userfiles/images/312640.jpg" style="height:169px; width:300px" /></span></span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm">&nbsp;</p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:11pt"><span style="font-family:Arial,sans-serif"><span style="font-size:14.0pt"><span style="font-family:Gungsuh">結束行前說明，主辦單位帶領體驗志工前往有機書店以及仙草田，實際整理民眾捐贈的書籍，將指定主題書籍挑出，讓書店員工可以進行後續上架，此外，每位志工都在結束後獲得一枝牛耕米冰棒，帶有黑糖風味的冰品，口感特殊。接著下個體驗活動，志工到南山大橋旁替仙草田除草、翻堆堆肥，撿拾掉落駁坎的垃圾。</span></span></span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm; text-align:center">&nbsp;</p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:14.0pt"><span style="font-family:Gungsuh">新竹縣志願服務推廣中心督導張淑婷期盼企業員工及親友透過本次體驗活動認識志工服務，在活動尾聲也一一頒發服務證明，鼓勵每位參與的夥伴，以及在現場發贈今年新發行的志願服務著色本，讓沒來參與志願服務活動的孩子，也能透過著本色了解志願服務內涵，傳遞「新竹人，喜助人」的美好價值。</span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm">&nbsp;</p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm"><span style="font-size:14.0pt"><span style="font-family:Gungsuh">採訪編輯：吳珮瑜</span></span></p>

<p style="margin-bottom:0.0001pt; margin-left:0cm; margin-right:0cm; margin-top:0cm">&nbsp;</p>

