INSERT [dbo].[Bulletins] ([Id], [title], [content],[NewsType], [StartDate], [EndDate], [CreateDate], [CreateBy], [ModifyDate], [ModifyBy], [IsDelete]) VALUES (578, N'【本縣志工單位介紹】001 新竹縣政府消防局', N'<p style="text-align:center"><span style="color:#2980b9"><span style="font-size:24px">新竹人喜助人</span></span></p>

<p style="text-align:center"><span style="color:#2980b9"><span style="font-size:24px"><img alt="" src="/FielUpLoad/userfiles/images/%E6%96%B0%E6%B9%96%E5%9C%B0%E6%94%BF%E4%BA%8B%E5%8B%99%E6%89%80.png" style="height:200px; width:140px" /><img alt="" src="/FielUpLoad/userfiles/images/%E6%96%B0%E7%AB%B9NPO_LOGO%20(1).png" style="height:200px; width:227px" /><img alt="" src="/FielUpLoad/userfiles/images/%E8%A1%9B%E7%94%9F%E5%B1%80.png" style="height:200px; width:140px" /></span></span></p>

<p style="text-align:center"><span style="color:#2980b9"><span style="font-size:24px">志工的領域好多元喔~</span></span></p>

<p style="text-align:center"><span style="color:#2980b9"><span style="font-size:24px">讓 喜助和喜妞 帶大家認識認識不同的志工單位吧!</span></span></p>

<p>&nbsp;</p>

<p><span style="font-size:24px">一、單位名稱：新竹縣消防局</span></p>

<p><span style="font-size:24px">二、服務類別：消防</span></p>

<p><span style="font-size:24px">三、單位簡介：</span></p>

<p><span style="font-size:24px">為落實住宅火災預防宣導工作，藉由組織成立「婦女防火宣導總隊」，針對居家用電、瓦斯安全及避難逃生等事項，深入家庭從事防火、防災宣導工作，希望此舉能夠降低住宅火災發生率、及其引發之傷亡率。<br />
