INSERT [dbo].[Bulletins] ([Id], [title], [content],[NewsType], [StartDate], [EndDate], [CreateDate], [CreateBy], [ModifyDate], [ModifyBy], [IsDelete]) VALUES (631, N'【訊息轉知】新竹縣政府辦理「2023年老人保護預防性關懷服務」招募老人關懷訪視員（以下簡稱老關員），請有意願的志工單位或個人踴躍參與', N'<p><span style="font-size:16px">新竹縣政府辦理「2023年老人保護預防性關懷服務」招募老人關懷訪視員（以下簡稱老關員），請有意願的志工單位或個人踴躍參與。</span></p>

